import React from 'react';
import { useState } from 'react';
import './App.css'

const App = () => {
    const result = useState(1)

    let counter = result[0];

    const lesHandler = ()=>{
        // alert('-')
        counter--;
    }

    const addHandler = ()=>{
        // alert('+')
        counter++;
    }

    return (
        <div className={'item'}>
            <h1>{counter}</h1>
            <button onClick={lesHandler}>-</button>
            <button onClick={addHandler}>+</button>
        </div>
    );
};

export default App;
