import React from 'react';
import './App.css'

const App = () => {
    let counter = 2;

    const lesHandler = ()=>{
        // alert('-')
        counter--;
    }

    const addHandler = ()=>{
        // alert('+')
        counter++;
    }

    return (
        <div className={'item'}>
            <h1>{counter}</h1>
            <button onClick={lesHandler}>-</button>
            <button onClick={addHandler}>+</button>
        </div>
    );
};

export default App;
